                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <PieChart className="h-5 w-5" />
                    Spending Categories
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {spendingStats ? (
                    <ResponsiveContainer width="100%" height={250}>
                      <RechartsPie>
                        <Pie
                          data={Object.entries(spendingStats).map(([category, value]) => ({ name: category, value }))}
