import { type Project, type InsertProject, type Milestone, type Risk } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Project CRUD
  getProjects(filters?: {
    status?: string;
    division?: string;
    dateFrom?: string;
    dateTo?: string;
  }): Promise<Project[]>;
  getProject(id: string): Promise<Project | undefined>;
  createProject(project: InsertProject): Promise<Project>;
  updateProject(id: string, updates: Partial<InsertProject>): Promise<Project | undefined>;
  deleteProject(id: string): Promise<boolean>;

  // Project statistics
  getProjectStats(): Promise<{
    totalProjects: number;
    activeProjects: number;
    completedProjects: number;
    delayedProjects: number;
    totalBudget: number;
    actualSpend: number;
    amountReceived: number;
  }>;

  // Chart data
  getSpendingCategoriesData(): Promise<{
    underBudget: number;
    withinBudget: number;
    overspent: number;
    criticalOverspent: number;
  }>;

  getProjectStatusData(): Promise<{
    aheadOfSchedule: number;
    onTrack: number;
    slightlyBehind: number;
    criticalDelay: number;
  }>;

  getDivisionData(): Promise<{
    mechanical: number;
    electrical: number;
    instrumentation: number;
  }>;
}

export class MemStorage implements IStorage {
  private projects: Map<string, Project>;

  constructor() {
    this.projects = new Map();
    this.seedData();
  }

  private seedData() {
